mapper.update:
update board set
boardTitle=#{boardTitle}, boardContent=#{boardContent}
where
boardSEQ=#{boardSEQ}

update fileinfo set
orgName = #{orgName}, savName = #{savName} where
boardSEQ=#{boardSEQ}

mapper.select:
select fileSEQ, boardSEQ, orgName, savName from fileinfo
where fileSEQ =
#{fileSEQ}
